    private void jButtonStateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonStateActionPerformed
        StringBuilder sb = new StringBuilder();
        sb.append("CollectData");
        sb.append(utilities.constants.token);
        String sentContent = clientConnection.sendContent(sb.toString() , this.jTextFieldIP.getText(), Integer.parseInt(this.jTextFieldPort.getText()));
        this.updateGUI(sentContent);
    }//GEN-LAST:event_jButtonStateActionPerformed

    
