    private void updateGUI(String sentContent) {
        StringTokenizer st = new StringTokenizer(sentContent, utilities.constants.token);
        String state = st.nextToken();
        String value = st.nextToken();
        String error = st.nextToken();
        //
        this.jTextFieldState.setText(state);
        this.jTextFieldProduction.setText(value);
        this.jTextFieldError.setText(error);
        //
        if(state.equals("1")){
            this.jLabelState.setText("ON");
        }else if(state.equals("0")){
            this.jLabelState.setText("OFF");
        }else{
            this.jLabelState.setText("Error");
        }
    }
